import React, { useState } from 'react';
import Discussion from './Discussion';
import classes from './TaskBank.module.css';

const TaskBank = () => {
  const [open, setOpen] = useState(false);
  const [taskName, setTaskName] = useState('');
  const [taskHref, setTaskHref] = useState('');
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);

  const handleAddTask = () => {
    const newTask = {
      name: taskName,
      creator: 'Имя автора', // Замените на актуальное имя
      hrefDisc: `discussion${tasks.length}`,
      taskHref: taskHref,
    };

    setTasks([...tasks, newTask]);

    setOpen(false);
    setTaskName('');
    setTaskHref('');
  };

  const handleOpenDiscussion = (taskName) => {
    setSelectedTask(taskName);
  };

  return (
    <div className={classes.TaskBank}>
      <h2 className={classes.TaskBankHeader}>банк задач</h2>
      <div className={classes.TaskBankTH}>
        <div className={classes.TaskTitle}>Предложенная задача</div>
        <div className={classes.TaskAuthor}>Автор</div>
      </div>
      <div className={classes.TasksContainer}>
        {tasks.map((task, index) => (
          <div key={index}>
            <a
              href={task.hrefDisc}
              onClick={() => handleOpenDiscussion(task.name)}
            >
              Обсуждение
            </a>
          </div>
        ))}
      </div>
      {open && (
        <div className={classes.Overlay}>
          <div className={classes.Modal}>
            <input
              name="taskName"
              type="text"
              value={taskName}
              onChange={(e) => setTaskName(e.target.value)}
            />
            <input
              name="taskHref"
              type="text"
              value={taskHref}
              onChange={(e) => setTaskHref(e.target.value)}
            />
            <button onClick={() => handleAddTask()}>Отправить</button>
          </div>
        </div>
      )}
      {selectedTask && (
        <Discussion TaskName={selectedTask} />
      )}
    </div>
  );
};

export default TaskBank;
