import './App.css';
import axios from 'axios';
import React from 'react';
import NewsDetails from './NewsDetails'; // импортируйте компонент NewsDetails

class App extends React.Component {
  state = {details: [], selectedCategory: null, showNewsDetails: false, selectedArticle: null}

  componentDidMount(){
    this.fetchArticles();
  }

  fetchArticles = (category) => {
    let url = 'http://127.0.0.1:8000/api/v1/articles/';
    if (category) {
      url += "?category=${category}";
    }
    axios.get(url)
    .then(res => {
      const data = res.data;
      this.setState({
        details: data,
        selectedCategory: category
      });
    })
    .catch(err => {
      console.log(err);
    });
  }

  handleClick = (article) => {
    this.setState({ showNewsDetails: true, selectedArticle: article });
  }

  render() {
    if (this.state.showNewsDetails) {
      return <NewsDetails article={this.state.selectedArticle} />;
    } else {
      return (
        <div>
          <h1>Data:</h1>
          <div>
            <button onClick={() => this.fetchArticles('archive')}>Архив</button>
            <button onClick={() => this.fetchArticles('news')}>Новости</button>
            <button onClick={() => this.fetchArticles('documents')}>Документы</button>
          </div>
          {this.state.details.map((output, id) => (
            <div key={id}>
              <p onClick={() => this.handleClick(output)}>{output.title}</p> {/* Добавьте обработчик клика */}
              <p>{output.description}</p>
            </div>
          ))}
        </div>
      );
    }
  }
}

export default App;
