import React, { useState } from 'react';
import Discussion from './Discussion';
import classes from './TaskBank.module.css';

const TaskBank = () => {
  const [open, setOpen] = useState(false);
  const [taskName, setTaskName] = useState('');
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null); // Состояние для отслеживания выбранной задачи

  const handleAddTask = () => {
    const newTask = {
      name: taskName,
      creator: 'Имя автора', // Замените на актуальное имя
      hrefDisc: '#',
      taskHref: "./pdf/archive1.pdf",
    };

    setTasks([...tasks, newTask]);

    setOpen(false);
    setTaskName('');
  };

  const handleOpenDiscussion = (taskName) => {
    setSelectedTask(taskName);
  };

  return (
    <div className={classes.TaskBank}>
      {/* ... остальной JSX TaskBank */}
      {tasks.map((task, index) => (
        <div key={index}>
          <a
            href={task.hrefDisc}
            onClick={() => handleOpenDiscussion(task.name)}
          >
            Обсуждение
          </a>
        </div>
      ))}

      {open && (
        <div className={classes.Overlay}>
          <div className={classes.Modal}>
            {/* ... остальной JSX для добавления задачи */}
            <button onClick={() => handleAddTask()}>Отправить</button>
          </div>
        </div>
      )}

      {selectedTask && <Discussion TaskName={selectedTask} />}
    </div>
  );
};

export default TaskBank;
