import classes from './TaskBank.module.css';
import Task from './TaskBank/Task';
import React from 'react';

const TaskBank = () => {
  const [open, setOpen] = React.useState(false);
  const [taskName, setTaskName] = React.useState('');

  const handleAddTask = () => {
    const newTask = document.createElement('div');
    newTask.classList.add('Task');    newTask.innerHTML = `
      <div class="TaskName">${taskName}</div>
      <div class="TaskCreator">Ваше имя</div>
      <a href="#" target="_blank" rel="noopener noreferrer">Ссылка на задачу</a>
    `;
    document.querySelector('.TasksContainer').appendChild(newTask);
    setOpen(false);
    setTaskName('');
  };

  return (
    <div className={classes.TaskBank}>
      <div className={classes.TaskBankContainer}>
        <h2 className={classes.TaskBankHeader}>банк задач</h2>
        <div className={classes.TaskBankTH}>
          <div className={classes.TaskTitle}>Предложенная задача</div>
          <div className={classes.TaskAuthor}>Автор</div>
        </div>
        <div className={classes.TasksContainer}>
          <Task hrefDisc={'#'} taskHref={'./Git.pdf'} taskName={'Тепловая задача'} taskCreator={'Петров А.А.'}/>
          <Task hrefDisc={'#'} taskHref={'./Git.pdf'} taskName={'Оптика'} taskCreator={'Иванов И.И.'}/>
          <Task hrefDisc={'#'} taskHref={'./Git.pdf'} taskName={'Кинематика'} taskCreator={'Рубцов Д.Н.'}/>
          <Task hrefDisc={'#'} taskHref={'# '} taskName={'Динамика лифта'} taskCreator={'Петров А.А.'}/>
          <Task hrefDisc={'#'} taskHref={'./Git.pdf'} taskName={'Электричество'} taskCreator={'Иванов И.И.'}/>
          <Task hrefDisc={'#'} taskHref={'./Git.pdf'} taskName={'Задача на логику'} taskCreator={'Петров А.А.'}/>
          <Task hrefDisc={'#'} taskHref={'./Git.pdf'} taskName={'Экспериментальная'} taskCreator={'Петров А.А.'}/>
        </div>
        <button onClick={() => setOpen(true)} className={classes.TaskBtn}>Добавить задачу</button>
        {open && (
          <div className={classes.Overlay}>
            <div className={classes.Modal}>
              <div className={classes.FileUpload}>
                <input name="upload" type="file"/>
                <div className={classes.Close} onClick={() => setOpen(false)} data-close>&times;</div>
              </div>
              <input name="taskName" type="text" value={taskName} onChange={(e) => setTaskName(e.target.value)} />
              <button onClick={handleAddTask}>Отправить</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};


export default TaskBank;
