// Task.js

import React from 'react';
import classes from './Task.module.css';

const Task = ({ task, onClickDiscussion }) => {
  const addTask = async () => {
    try {
      // Проверяем, что task существует и содержит свойство content
      if (task && task.content) {
        // Создаем форму для отправки файла
        const formData = new FormData();
        formData.append('title', task.title);
        formData.append('content', task.content);

        const response = await fetch('http://127.0.0.1:8000/api/v1/tasks/create/', {
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          console.log('Задача успешно добавлена');
          // Добавьте код для обновления списка задач или других необходимых действий
        } else {
          console.error('Ошибка при добавлении задачи');
        }
      } else {
        console.error('Некорректные данные задачи');
      }
    } catch (error) {
      console.error('Ошибка при выполнении запроса:', error);
    }
  };

  return (
    <div className={classes.Task}>
      <a href={task.content} className={classes.TaskName}>
        {task.title}
      </a>
      <div className={classes.TaskСreator}>{task.username}</div>
      <button onClick={addTask} className={classes.TaskDiscussionBtn}>
        Добавить задачу
      </button>
      <button onClick={() => onClickDiscussion(task.title)} className={classes.TaskDiscussionBtn}>
        Обсуждение
      </button>
    </div>
  );
};

export default Task;
