import './App.css';
import axios from 'axios';
import React from 'react';

class App extends React.Component {
  state = {details: [], selectedCategory: null}

  componentDidMount(){
    this.fetchArticles();
  }

  fetchArticles = (category) => {
    let url = 'http://127.0.0.1:8000/api/v1/tasks/';
    if (category) {
      url += `?category=${category}`;
    }
    axios.get(url)
    .then(res => {
      const data = res.data;
      this.setState({
        details: data,
        selectedCategory: category
      });
    })
    .catch(err => {
      console.log(err);
    });
  }

  render() {
    return (
      <div className="container">
        <h1>Банк задач</h1>
        <div className="btn-container">
          <button onClick={() => this.fetchArticles('archive')}>Архив</button>
          <button onClick={() => this.fetchArticles('news')}>Новости</button>
          <button onClick={() => this.fetchArticles('documents')}>Документы</button>
        </div>
        <div className="details-container">
          {this.state.details.map((output, id) => (
            <div key={id} className="details-row">
              <a href={output.content}>{output.title}</a>
              <p>{output.username}</p>
            </div>
          ))}
        </div>
      </div>
    )
  }
}

export default App;
