import classes from './Discussion.module.css';

const Discussion = ({TaskName}) => {
  return (
    <section className={classes.Discussion}>
      <div className={classes.DiscussionContainer}>
      <div className={classes.DiscussionTitle}>{TaskName}</div>
      <div className=""></div>
      <DiscussionContent comment={'Александр Аркадьевич, хорошая задача! Предлагаю на заключительный этап олимпиады в 7 класс'} commentPerson={'Рубцов Д.Н.'}/>
      
      </div>
    </section>
  )
}

export default Discussion;

const DiscussionContent = ({commentPerson, comment}) => {
  return(
    <div className={classes.DiscussionContent}>
        <div className={classes.CommentPerson}>{commentPerson}</div>
        <div className={classes.Comment}>{comment}</div>
      </div>
  )
}
