import React, { useState } from 'react';
import classes from './Task.module.css';
import DiscussionSection from './DiscussionSection';

const Task = ({ taskName, taskCreator, taskHref }) => {
  const [showDiscussion, setShowDiscussion] = useState(false);

  const handleDiscussionClick = () => {
    setShowDiscussion(true);
  };

  return (
    <div id='newTask' className={classes.Task}>
      <a href={taskHref} className={classes.TaskName}>
        {taskName}
      </a>
      <div className={classes.TaskСreator}>{taskCreator}</div>
      <button onClick={handleDiscussionClick} className={classes.TaskDiscussionBtn}>
        Обсуждение
      </button>
      {showDiscussion && <DiscussionSection />}
    </div>
  );
};

export default Task;
