import classes from './Discussion.module.css';

const Discussion = ({TaskName}) => {
  return (
    <section className={classes.Discussion}>
      <div className={classes.DiscussionContainer}>
      <div className={classes.DiscussionTitle}>{TaskName}</div>
      <div className={classes.Discussions}>
        {/* <DiscussionContent comment={'Александр Аркадьевич, хорошая задача! Предлагаю на заключительный этап олимпиады в 7 класс'} commentPerson={'Рубцов Д.Н.'}/>
        <DiscussionContent comment={'Коллеги! Если добавить в задачу еще один вопрос, можно дать и 9классникам! Заметила также, что в задаче есть несколько опечаток'} commentPerson={'Петров Е.В.'}/>
        <DiscussionContent comment={'Товарищи! Спасибо за обратную связь! Буду исправлять опечатки и пытаться усложнить задачу)'} commentPerson={'Денисов П.А.'}/> */}
      </div>
      </div>
    </section>
  )
}

export default Discussion;

