import React from 'react';
import classes from './Task.module.css';

const Task = ({ taskName, taskCreator, taskHref, hrefDisc, onClickDiscussion }) => {
  return (
    <div id='newTask' className={classes.Task}>
      <a href={taskHref} className={classes.TaskName}>{taskName}</a>
      <div className={classes.TaskСreator}>{taskCreator}</div>
      <button onClick={() => onClickDiscussion(taskName)} className={classes.TaskDiscussionBtn}>
        Обсуждение
      </button>
    </div>
  )
}

export default Task;
