import React, { useState } from 'react';
import classes from './Form.module.css';
import FormBtn from '../Buttons/FormButtons/FormBtn';

const Form = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/api/v1/token/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      // Обрабатывайте здесь ответ от сервера
      if (response.ok) {
        const data = await response.json();
        console.log('Успешный ответ:', data);
        // Добавьте код для обработки успешного ответа, например, сохранение токена
      } else {
        console.error('Ошибка при отправке данных:', response.status);
        // Добавьте код для обработки ошибки, например, отображение сообщения пользователю
      }
    } catch (error) {
      console.error('Ошибка:', error.message);
      // Добавьте код для обработки ошибки, например, отображение сообщения пользователю
    }
  };

  return (
    <form className={classes.Form} onSubmit={handleSubmit}>
      <input
        className={classes.FormInput}
        name='username'
        required
        placeholder='Ваше имя'
        type='text'
        value={formData.username}
        onChange={handleInputChange}
      />
      <input
        className={classes.FormInput}
        name='password'
        required
        placeholder='Ваш пароль'
        type='password'
        value={formData.password}
        onChange={handleInputChange}
      />
      <FormBtn />
    </form>
  );
};

export default Form;
