import React, { useState, useEffect } from 'react';
import classes from './TaskBank.module.css';
import Task from './TaskBank/Task';
import Discussion from './Discussion';

const TaskBank = () => {
  const [open, setOpen] = useState(false);
  const [taskName, setTaskName] = useState('');
  const [file, setFile] = useState(null);
  const [tasks, setTasks] = useState([]); // Храним список задач

  const [discussionVisible, setDiscussionVisible] = useState(false);
  const [selectedTaskName, setSelectedTaskName] = useState('');
  const [comments, setComments] = useState([]);

  // Загрузка списка задач при монтировании компонента
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/v1/tasks/');
        if (response.ok) {
          const tasksData = await response.json();
          setTasks(tasksData);
        } else {
          console.error('Ошибка при загрузке задач');
        }
      } catch (error) {
        console.error('Ошибка при выполнении запроса:', error);
      }
    };

    fetchTasks();
  }, []); // Пустой массив зависимостей гарантирует выполнение эффекта только один раз при монтировании

  const handleAddTask = async () => {
    try {
      const formData = new FormData();
      formData.append('title', taskName);
      formData.append('content', file);

      // Получаем CSRF-токен из cookies
      const csrfToken = document.cookie.split("; ").find(row => row.startsWith("csrftoken=")).split("=")[1];

      const response = await fetch('http://127.0.0.1:8000/api/v1/tasks/create/', {
        method: 'POST',
        body: formData,
        headers: {
          'X-CSRFToken': csrfToken,
        },
      });

      if (response.ok) {
        console.log('Задача успешно добавлена');
        // После успешного добавления задачи, обновите список задач
        const newTask = {
          id: tasks.length + 1, // Ваша логика генерации id
          name: taskName,
          creator: 'Имя автора', // Замените на актуальное имя
          hrefDisc: '#',
          taskHref: "./pdf/archive1.pdf",
        };
        setTasks([...tasks, newTask]);

        setOpen(false);
        setTaskName('');
        setFile(null);
      } else {
        console.error('Ошибка при добавлении задачи');
      }
    } catch (error) {
      console.error('Ошибка при выполнении запроса:', error);
    }
  };

  const handleDiscussionClick = (taskName) => {
    setSelectedTaskName(taskName);
    setDiscussionVisible(true);
    setComments([]);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
  };

  return (
    <div className={classes.TaskBank}>
      <div className={classes.TaskAuthorName}>Вы вошли в систему под именем</div>
      <div className={classes.TaskBankContainer}>
        <h2 className={classes.TaskBankHeader}>банк задач</h2>
        <div className={classes.TaskBankTH}>
          <div className={classes.TaskTitle}>Предложенная задача</div>
          <div className={classes.TaskAuthor}>Автор</div>
        </div>
        <div className={classes.TasksContainer}>
          {tasks.map((task, index) => (
            <Task
              key={index}
              task={task}
              onClickDiscussion={handleDiscussionClick}
            />
          ))}
        </div>
        <button onClick={() => setOpen(true)} className={classes.TaskBtn}>
          Добавить задачу
        </button>
        {open && (
          <div className={classes.Overlay}>
            <div className={classes.Modal}>
              <div className={classes.FileUpload}>
                <input accept='.pdf' name="upload" type="file" onChange={handleFileChange} />
                <div
                  className={classes.Close}
                  onClick={() => setOpen(false)}
                  data-close
                >
                  &times;
                </div>
              </div>
              <input
                placeholder='Введите название задачи'
                name="taskName"
                type="text"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
              />
              <button onClick={handleAddTask}>Отправить</button>
            </div>
          </div>
        )}
        {discussionVisible && <Discussion TaskName={selectedTaskName} setDiscussionVisible={setDiscussionVisible} comments={comments} setComments={setComments} />}
      </div>
    </div>
  );
};

export { TaskBank };
