import React, { useState } from 'react';
import axios from 'axios';
import classes from './Form.module.css';
import FormBtn from '../Buttons/FormButtons/FormBtn';

const Form = ({ onLogin, onRegister }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/v1/token/', {
        username,
        password,
      });

      if (response.data.access) {
        // Если у response есть access, вызываем onLogin с именем пользователя
        onLogin(username);
      } else {
        console.error('Ошибка авторизации');
      }
    } catch (error) {
      console.error('Произошла ошибка при запросе на сервер:', error);
    }
  };

  const handleRegister = async () => {
    try {
      await axios.post('http://127.0.0.1:8000/api/v1/users/register/', {
        username,
        password,
      });

      // После успешной регистрации, вызываем onRegister с именем пользователя
      onRegister(username);
    } catch (error) {
      console.error('Произошла ошибка при запросе на сервер:', error);
    }
  };

  return (
    <form className={classes.Form} action="#">
      <input
        className={classes.FormInput}
        name='username'
        required
        placeholder='Ваше имя'
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        className={classes.FormInput}
        name='password'
        required
        placeholder='Ваш пароль'
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <FormBtn onLogin={handleLogin} onRegister={handleRegister} />
    </form>
  );
};

export default Form;
