// Task.js

import React from 'react';
import classes from './Task.module.css';

const Task = ({ task, onClickDiscussion }) => {
  return (
    <div className={classes.Task}>
      <a href={task.taskHref} className={classes.TaskName}>
        {task.name}
      </a>
      <div className={classes.TaskСreator}>{task.creator}</div>
      <button onClick={() => onClickDiscussion(task.name)} className={classes.TaskDiscussionBtn}>
        Обсуждение
      </button>
    </div>
  );
};

export default Task;
