import React, { useState, useEffect } from 'react';
import classes from './Discussion.module.css';

const Discussion = ({ TaskName, setDiscussionVisible }) => {
  const [comments, setComments] = useState([]);
  const [comment, setComment] = useState('');

  // Загрузка комментариев при монтировании компонента
  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/v1/comments/')
      .then(response => response.json())
      .then(data => {
        console.log('Комментарии успешно загружены:', data);
        setComments(data);
      })
      .catch(error => {
        console.error('Ошибка при загрузке комментариев:', error);
      });
  }, []); // Пустой массив зависимостей, чтобы эффект выполнялся только при монтировании компонента

  const handleAddComment = () => {
    if (comment) {
      const newComment = {
        commentPerson: 'Пользователь',
        comment: comment,
      };

      fetch('http://127.0.0.1:8000/api/v1/comments/create/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newComment),
      })
      .then(response => response.json())
      .then(data => {
        console.log('Комментарий успешно добавлен:', data);
        const updatedComments = [...comments, data];
        setComments(updatedComments);
        setComment('');
      })
      .catch(error => {
        console.error('Ошибка при добавлении комментария:', error);
      });
    }
  };

  const handleCloseDiscussion = () => {
    setDiscussionVisible(false);
  };

  return (
    <section className={classes.Discussion}>
      <div className={classes.DiscussionContainer}>
        <button onClick={handleCloseDiscussion} className={classes.CloseButton}>
          &times;
        </button>
        <div className={classes.DiscussionTitle}>{TaskName}</div>
        <div className={classes.Discussions}>
          {comments.map((comment, index) => (
            <div key={index} className={classes.DiscussionContent}>
              <div className={classes.CommentPerson}>{comment.commentPerson}</div>
              <div className={classes.Comment}>{comment.comment}</div>
            </div>
          ))}
        </div>
      </div>
      <div className={classes.CommentInputContainer}>
        <textarea
          className={classes.CommentInput}
          placeholder="Введите комментарий"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />
        <button onClick={handleAddComment} className={classes.DiscussionBtn}>
          Добавить комментарий
        </button>
      </div>
    </section>
  );
};

export default Discussion;
