import React, { useState, useEffect } from 'react';
import axios from 'axios';
import classes from './Discussion.module.css';

const Discussion = ({ taskId, setDiscussionVisible, comments, setComments }) => {
  const [comment, setComment] = useState('');

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/api/v1/comments/?task=${taskId}`);
        if (response.status === 200) {
          setComments(response.data);
        } else {
          console.error('Ошибка при загрузке комментариев');
        }
      } catch (error) {
        console.error('Ошибка при выполнении запроса:', error);
      }
    };
  
    fetchComments();
  }, [taskId, setComments]);
  
  const handleAddComment = async () => {
    if (comment) {
      try {
        const response = await axios.post('http://127.0.0.1:8000/api/v1/comments/', {
          task: taskId,
          content: comment,
          username: 'root', // Замените на актуальное имя пользователя
        });

        if (response.status === 201) {
          const newComment = {
            user: response.data.user,
            task: response.data.task,
            content: response.data.content,
            create: response.data.create,
            username: response.data.username,
          };

          const updatedComments = [...comments, newComment];
          setComments(updatedComments);
          setComment('');
        } else {
          console.error('Ошибка при добавлении комментария');
        }
      } catch (error) {
        console.error('Ошибка при выполнении запроса:', error);
      }
    }
  };

  const handleRefreshComments = () => {
    // Метод для обновления комментариев
    fetchComments();
  };

  const handleCloseDiscussion = () => {
    setDiscussionVisible(false);
  };

  return (
    <section className={classes.Discussion}>
      <div className={classes.DiscussionContainer}>
        <button onClick={handleCloseDiscussion} className={classes.CloseButton}>
          &times;
        </button>
        <div className={classes.DiscussionTitle}>Комментарии к задаче #{taskId}</div>
        <div className={classes.Discussions}>
          {comments.map((comment, index) => (
            <div key={index} className={classes.DiscussionContent}>
              <div className={classes.CommentPerson}>{comment.username}</div>
              <div className={classes.Comment}>{comment.content}</div>
            </div>
          ))}
        </div>
      </div>
      <div className={classes.CommentInputContainer}>
        <textarea
          className={classes.CommentInput}
          placeholder="Введите комментарий"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />
        <button onClick={handleAddComment} className={classes.DiscussionBtn}>
          Добавить комментарий
        </button>
        <button onClick={handleRefreshComments} className={classes.RefreshBtn}>
          Обновить комментарии
        </button>
      </div>
    </section>
  );
};

export default Discussion;
