import React, { useState } from 'react';
import axios from 'axios';
import classes from './Form.module.css';
import FormBtn from '../Buttons/FormButtons/FormBtn';

const Form = () => {
  const [showThanks, setShowThanks] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    // Реализация запроса на авторизацию
  };

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/v1/users/register/', {
        username,
        password,
      });

      if (response.status === 201) {
        setShowThanks(true);
        setTimeout(() => {
          setShowThanks(false);
        }, 3000);
      }
    } catch (error) {
      // Обработка ошибок регистрации
      console.error('Ошибка при регистрации:', error);
    }
  };

  return (
    <div>
      <form className={classes.Form} action="#">
        <input
          className={classes.FormInput}
          name='username'
          required
          placeholder='Ваше имя'
          type='text'
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          className={classes.FormInput}
          name='password'
          required
          placeholder='Ваш пароль'
          type='password'
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <FormBtn onLogin={handleLogin} onRegister={handleRegister} />
      </form>
      {showThanks && <div className={classes.ThanksMessage}>Спасибо за регистрацию!</div>}
    </div>
  );
};

export default Form;
