import React, { useState } from 'react';
import axios from 'axios';
import classes from './Form.module.css';
import FormBtn from '../Buttons/FormButtons/FormBtn';

const Form = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false);

  const handleLogin = async () => {
    // ... код обработки входа
  };

  const handleRegister = async () => {
    try {
      await axios.post('http://127.0.0.1:8000/api/v1/users/register/', {
        username,
        password,
      });

      // Устанавливаем локальное состояние, чтобы отобразить сообщение об успешной регистрации
      setRegistrationSuccess(true);

      // Вызываем onLogin с именем пользователя
      onLogin(username);

      // Через 3000 миллисекунд (3 секунды) скрываем сообщение об успешной регистрации
      setTimeout(() => {
        setRegistrationSuccess(false);
      }, 3000);
    } catch (error) {
      console.error('Произошла ошибка при запросе на сервер:', error);
    }
  };

  return (
    <div className={classes.Form}>
      <input
        className={classes.FormInput}
        name='username'
        required
        placeholder='Ваше имя'
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        className={classes.FormInput}
        name='password'
        required
        placeholder='Ваш пароль'
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <FormBtn onLogin={handleLogin} onRegister={handleRegister} />
      {registrationSuccess && (
        <div className={classes.RegistrationSuccess}>Спасибо за регистрацию!</div>
      )}
    </div>
  );
};

export default Form;
