  import React, { useState, useEffect } from 'react';
  import axios from 'axios';
  import classes from './Discussion.module.css';

  const Discussion = ({ taskID, TaskName, setDiscussionVisible, comments, setComments }) => {
    const [comment, setComment] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchComments = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://127.0.0.1:8000/api/v1/comments/?task=${taskID}`);
        setComments(response.data);
      } catch (error) {
        setError(error.message || 'Произошла ошибка при загрузке комментариев.');
      } finally {
        setLoading(false);
      }
    };

    useEffect(() => {
      // Загружаем комментарии при монтировании компонента
      fetchComments();
    }, [taskID]); // Запускаем загрузку комментариев при изменении ID задачи

    const handleAddComment = async () => {
      if (comment) {
        try {
          setLoading(true);
          const newComment = {
            user: 1, // Замените на актуальный идентификатор пользователя
            task: taskID,
            content: comment,
          };
          await axios.post('http://127.0.0.1:8000/api/v1/comments/', newComment);
          // После успешного добавления комментария, загружаем обновленный список комментариев
          fetchComments();
          setComment('');
        } catch (error) {
          setError(error.message || 'Произошла ошибка при добавлении комментария.');
        } finally {
          setLoading(false);
        }
      }
    };

    const handleCloseDiscussion = () => {
      setDiscussionVisible(false);
    };

    return (
      <section className={classes.Discussion}>
        <div className={classes.DiscussionContainer}>
          <button onClick={handleCloseDiscussion} className={classes.CloseButton}>
            &times;
          </button>
          <div className={classes.DiscussionTitle}>{TaskName}</div>
          {loading && <p>Загрузка комментариев...</p>}
          {error && <p>{error}</p>}
          <div className={classes.Discussions}>
            {comments.map((comment, index) => (
              <div key={index} className={classes.DiscussionContent}>
                <div className={classes.CommentPerson}>{comment.username}</div>
                <div className={classes.Comment}>{comment.content}</div>
              </div>
            ))}
          </div>
        </div>
        <div className={classes.CommentInputContainer}>
          <textarea
            className={classes.CommentInput}
            placeholder="Введите комментарий"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
          <button onClick={handleAddComment} className={classes.DiscussionBtn}>
            Добавить комментарий
          </button>
        </div>
      </section>
    );
  };

  export default Discussion;
