import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Шаг 1: Импорт useHistory
import classes from './Form.module.css';
import FormBtn from '../Buttons/FormButtons/FormBtn';

const Form = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false);
  const [registrationError, setRegistrationError] = useState(false);
  const history = useNavigate(); 

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/v1/token/', {
        username,
        password,
      });

      if (response.data.access) {  // Шаг 3: Проверка наличия токена доступа
        // Шаг 4: Вызов onLogin с именем пользователя
        onLogin(username);
        
        // Шаг 4: Перенаправление пользователя
        history.push('/welcome');  // Замените '/welcome' на путь, куда вы хотите перенаправить пользователя
      }
    } catch (error) {
      console.error('Произошла ошибка при запросе на сервер:', error);
    }
  };

  const handleRegister = async () => {
    // ... код обработки регистрации
  };

  return (
    <div className={classes.Form}>
      <input
        className={classes.FormInput}
        name='username'
        required
        placeholder='Ваше имя'
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        className={classes.FormInput}
        name='password'
        required
        placeholder='Ваш пароль'
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <FormBtn onLogin={handleLogin} onRegister={handleRegister} />
      {registrationSuccess && (
        <div className={classes.RegistrationSuccess}>Спасибо за регистрацию!</div>
      )}
      {registrationError && (
        <div className={classes.RegistrationError}>Пожалуйста, заполните форму регистрации.</div>
      )}
    </div>
  );
};

export default Form;
