import React, { useState } from 'react';
import classes from './Discussion.module.css';



const Discussion = ({ TaskName, setDiscussionVisible, comments, setComments}) => {


   // Состояние для хранения массива комментариев
   const [comment, setComment] = useState('');
  const handleAddComment = () => {
    // При нажатии на кнопку "Добавить комментарий" добавляем новый комментарий в массив
    if (comment) {
      const newComment = {
        commentPerson: 'Пользователь', // Замените на актуальное имя пользователя
        comment: comment,
      };
      const updatedComments = [...comments, newComment];
      setComments(updatedComments);
      setComment(''); // Очищаем текст комментария после добавления
    }
  };

  const handleCloseDiscussion = () => {
    setDiscussionVisible(false); // вызываем функцию setDiscussionVisible, чтобы скрыть окно discussion
  };

  return (
    <section className={classes.Discussion}>
      <div className={classes.DiscussionContainer}>
      <button onClick={handleCloseDiscussion}  className={classes.CloseButton}>
            &times;
      </button>
        <div className={classes.DiscussionTitle}>{TaskName}</div>
        <div className={classes.Discussions}>
          {comments.map((comment, index) => (
            <div key={index} className={classes.DiscussionContent}>
              <div className={classes.CommentPerson}>{comment.commentPerson}</div>
              <div className={classes.Comment}>{comment.comment}</div>
            </div>
          ))}
        </div>
      </div>
      <div className={classes.CommentInputContainer}>
        <textarea
          className={classes.CommentInput}
          placeholder="Введите комментарий"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />
        <button onClick={handleAddComment} className={classes.DiscussionBtn}>
          Добавить комментарий
        </button>
      </div>
    </section>
  );
};

export default Discussion;
