import React from 'react';
import { BrowserRouter, Route, NavLink, Link, Switch } from 'react-router-dom';

import Archive from '../ArchiveSection';
import News from '../NewsSection';
import Documents from '../Documents';
import classes from './Container.module.css';

const routes = [
  { path: '/', exact: true, component: Buttons },
  { path: '/archive', component: Archive },
  { path: '/news', component: News },
  { path: '/documents', component: Documents },
];

const handleButtonClick = (path) => {
  // Ваша логика обработки клика на кнопку
  console.log(`Button clicked for path: ${path}`);
};

const Buttons = () => (
  <div>
    {/* Пример кнопки с использованием Link */}
    <Link to="/archive">
      <button onClick={() => handleButtonClick('/archive')}>Архив заданий</button>
    </Link>
  </div>
);

const App = () => (
  <BrowserRouter>
    <div className={classes.App}>
      <header className={classes.Header}>
        <h1>Контакты</h1>
      </header>
      <main className={classes.Main}>
        <Switch>
          <Route exact path="/" component={Buttons} />
          {routes.map((route) => (
            <Route key={route.path} path={route.path} component={route.component} />
          ))}
        </Switch>
        <nav>
          <NavLink exact to="/" activeClassName="active">
            Архив заданий регионального этапа ВсОШ по физике
          </NavLink>
          <NavLink to="/news" activeClassName="active">
            Новости
          </NavLink>
          <NavLink to="/documents" activeClassName="active">
            Документы
          </NavLink>
        </nav>
      </main>
      <footer className={classes.Footer}>
        <p>Контакты</p>
      </footer>
    </div>
  </BrowserRouter>
);

export default App;
