import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { NewsSection } from './NewsSection';
import { ArchiveSection } from './ArchiveSection';
import { Documents } from './Documents';
import Discussion from './Discussion';

const RouterSection = () => {
  return(
    <Routes>
      <Route path="/archive" element={<ArchiveSection/>}/>
      <Route path="/news" element={<NewsSection/>}/>
      <Route path="/documents" element={<Documents/>}/>
    </Routes> 
  );
};

const DiscussionRouterSection = () => {
  // Пример в Container.js

  return (
    <div>
      <Routes>
        <Route path="/discussion" element={<DiscussionSection />} />
        {/* Другие маршруты */}
      </Routes>
      <Task
        taskName="Название вашей задачи"
        taskCreator="Создатель задачи"
        taskHref="/детали-задачи"
      />
    </div>
  );
};


export {RouterSection, DiscussionRouterSection};
